import React,{Component} from 'react';
import { View, Button, Alert, Animated, ActivityIndicator, FlatList, Modal } from 'react-native';
export default class DemoCon extends Component{
        state = {
            fadeAnim: new Animated.Value(0),
            DATA:[
                {
                    id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
                    title: 'First Item',
                },
                {
                    id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
                    title: 'Second Item',
                },
                {
                    id: '58694a0f-3da1-471f-bd96-145571e29d72',
                    title: 'Third Item',
                }
            ],
            sureModal: false
        }
        fadeIn = ()=>{
            Animated.timing(this.state.fadeAnim, {
                toValue: 1,
                duration: 2000
            }).start()
        }
        fadeOut = ()=>{
            Animated.timing(this.state.fadeAnim, {
                toValue: 0,
                duration: 2000
            }).start()
        }
    BtnDemo(){
        Alert.alert('弹窗')
    }
    setSureModel = (sure)=>{
        this.setState({sureModal: sure});
    }
    render(){
        const sureModal = this.state.sureModal;
        return (
            <View>
                <Animated.View
                    style={{opacity:this.state.fadeAnim}}
                >
                    Animation
                </Animated.View>
                <Button onPress={this.fadeIn} title="fadeIn"></Button>
                <Button onPress={this.fadeOut} title="fadeOut"></Button>
                <ActivityIndicator size="large" color="#0000ff" animating={true} hidesWhenStopped={true}></ActivityIndicator>
                {/* animating是指定是否显示动画，hidesWhenStopped当animating为false，是否显示指示器 */}
                <FlatList data={this.state.DATA} renderItem={({item})=>{<Text title={item}></Text>}} keyExtractor={item => item.id}></FlatList>

                <Button onPress={()=>{this.setSureModel(true)}}>显示modal</Button>
                <Modal visible={sureModal}>
                    <View>
                        <Text>modal窗口</Text>
                    </View>

                </Modal>
            </View>
        )
    }
}
