import React,{Component} from 'react';
import { View, Button, Alert, Animated } from 'react-native';
export default class DemoCon extends Component{
    constructor(props){
        super(props)
        state = {
            fadeAnim
        }
        Animated.timing(this.state.fadeAnim, {
            toValue: 1,
            easing: Easing.back(),
            duration: 2000
          }).start();
    }
    BtnDemo(){
        Alert.alert('弹窗')
    }
    render(){
        return (
            <View>
                <Animated.View
                    style={{opacity:fadeAnim}}
                >
                    Animation
                </Animated.View>
                <Button onPress={this.BtnDemo} title="press me"></Button>
            </View>
        )
    }
}
